mapper.select:
SELECT rcp_li.* FROM (SELECT rcp.*, rownum rm FROM (SELECT * FROM produce_board
WHERE produce_type=#{produce_type}
ORDER BY produce_num DESC)rcp)rcp_li
WHERE rm >= #{startRow} AND rm <= #{endRow}

SELECT rcp_li.* FROM (SELECT rcp.*, rownum rm FROM (SELECT produce_board.produce_num, produce_board.user_id, produce_board.produce_type, produce_board.produce_name, produce_board.price, produce_board.produce_image, produce_board.produce_image_path, produce_board.produce_description
FROM produce_board, pay_history WHERE pay = 1 AND produce_board.produce_num = pay_history.produce_num GROUP BY produce_board.produce_num, produce_board.user_id, produce_board.produce_type, produce_board.produce_name, produce_board.price, produce_board.produce_image, produce_board.produce_image_path, produce_board.produce_description ORDER BY sum(quantity) DESC
FROM produce_board, pay_history, produce_review WHERE pay=1 AND produce_review.pay_num = pay_history.pay_num AND produce_board.produce_num = pay_history.produce_num GROUP BY produce_review.rating, produce_board.produce_num, produce_board.user_id, produce_board.produce_type, produce_board.produce_name, produce_board.price, produce_board.produce_image, produce_board.produce_image_path, produce_board.produce_description ORDER BY avg(produce_review.rating) DESC
)rcp)rcp_li
WHERE rm >= #{startRow} AND rm <= #{endRow}

mapper.update:
UPDATE produce_board
SET produce_type=#{produce_type}, produce_name=#{produce_name}, price=#{price}, produce_description=#{produce_description}
WHERE produce_num=#{produce_num}
