mapper.select:
SELECT rcp_li.* FROM (SELECT rcp.*, rownum rm FROM (SELECT * FROM recipe
WHERE rcp_name LIKE '%' || #{searchKey} || '%' OR rcp_parts_dtls LIKE '%' || #{searchKey} || '%' OR summary LIKE '%' || #{searchKey} || '%' OR hash_tag LIKE '%' || #{searchKey} || '%'
ORDER BY rcp_seq DESC)rcp)rcp_li WHERE rm >= #{startRow} AND rm <= #{endRow}
